import { useEffect, useState } from 'react';
import './InputTable.css';
import ReactDom from 'react-dom/client';
function InputTable(props){

    const [DataList,SetDataList]=useState('');

    useEffect(()=>{
        if(props){
            SetDataList(props);
        }
    },[props]);

    return(
            <div className="table-container">
                <h2>Data Table</h2>
                <table id="data-table">
                    <thead>
                        <tr>
                            <th>Select</th>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Phone Number</th>
                            <th>Email</th>
                            <th>Hobbies</th>
                        </tr>
                    </thead>
                    <tbody>
                        {/* {DataList.map((item,index)=>(
                            <tr key={index}>
                            <td><input type="checkbox" /> </td>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.phone}</td>
                            <td>{item.email}</td>
                            <td>{item.hobby}</td>
                            </tr>
                        ))} */}
                        {DataList.map((item,index)=>(
                        <tr key={index}>
                            <td><input type="checkbox" /> </td>
                            <td>{props.id}</td>
                            <td>{props.name}</td>
                            <td>{props.phone}</td>
                            <td>{props.email}</td>
                            <td>{props.hobby}</td>
                        </tr>
                        ))}
                    </tbody>
                </table>
                <div className="button-container">
                    <button id="update-btn">Update</button>
                    <button id="delete-btn">Delete</button>
                </div>
            </div>
    );
}

export default InputTable;
