import { useState } from 'react';
import './InputForm.css';


function InputForm(){
    let name,email,phone,hobby;
    const[enterName,SetName]=useState('');
    const[enterPhone,SetPhone]=useState('');
    const[enterEmail,SetEmail]=useState('');
    const[enterHobby,SetHobby]=useState('');


    function NameHandler(event)
    {
        console.log(event.target.value);
    }
    function PhoneHandler(event)
    {
        console.log(event.target.value);
    }
    function EmailHandler(event)
    {
        console.log(event.target.value);
    }
    function HobbyHandler(event)
    {
        console.log(event.target.value);
    }


    return(
            <div class="container">
                <div class="form-container glow">
                <h2>RedPositive Task</h2>
                    <form id="entry-form">
                        <div class="form-field">
                            <label for="name">Name:</label>
                            <input type="text" id="name" required onClick={NameHandler} />
                        </div>
                        <div class="form-field">
                            <label for="phone">Phone Number:</label>
                            <input type="tel" id="phone" pattern="[0-9]+" maxlength="12" minlength="10" required onClick={PhoneHandler}  />
                        </div>
                        <div class="form-field">
                            <label for="email">Email:</label>
                            <input type="email" id="email" required onClick={EmailHandler} />
                        </div>
                        <div class="form-field">
                            <label for="hobbies">Hobbies:</label>
                            <input type="text" id="hobbies"onClick={HobbyHandler}  />
                        </div>
                <button type="submit">Save</button>
                    </form>
                </div>  
            </div>      
    );
}

export default InputForm;
