import React, { useEffect, useState } from 'react';
import './InputTable.css';



function InputTable(props) {
  const [dataList, setDataList] = useState([]);

  useEffect(() => {
    if (props.data) {
      setDataList(props.data);
    }
  }, [props.data]);




  const [selectedRows, setSelectedRows] = useState([]);
  
  const handleCheckboxChange = (itemId) => {
    if (selectedRows.includes(itemId)) {
      setSelectedRows(selectedRows.filter((id) => id !== itemId));
    } else {
      setSelectedRows([...selectedRows, itemId]);
    }
  };

  const handleUpdate = () => {
    // Perform update operation on selectedRows
    console.log("Updating rows:", selectedRows);
  };

  const handleDelete = () => {
    // Perform delete operation on selectedRows
    console.log("Deleting rows:", selectedRows);
  };




  return (
    <div className="table-container">
      <h2>Data Table</h2>
      <table id="data-table">
        <thead>
          <tr>
            <th>Select</th>
            <th>ID</th>
            <th>Name</th>
            <th>Phone Number</th>
            <th>Email</th>
            <th>Hobbies</th>
          </tr>
        </thead>
        <tbody>
          {dataList.length > 0 ? (
            dataList.map((item) => (
              <tr key={item.id}>
                <td>
                  <input type="checkbox" />
                </td>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.phone}</td>
                <td>{item.email}</td>
                <td>{item.hobby}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No data available</td>
            </tr>
          )}
        </tbody>
      </table>
      <div className="button-container">
        <button onClick={handleUpdate} disabled={selectedRows.length === 0}>
          Update
        </button>
        <button onClick={handleDelete} disabled={selectedRows.length === 0}>
          Delete
        </button>
      </div>
    </div>
  );
}

export default InputTable;
